def eliminateDuplicates(word):
    a = {}
    s = ""
    for x in range(65, 91):
        a[chr(x)] = 0
    for x in range(97, 123):
        a[chr(x)] = 0

    for letter in word:
        if a[letter] == 0:
            s+=letter
            a[letter] = 1

    return s


def merge_the_tools(string, k):
    for x in range(k,len(string)+1,k):
        print(eliminateDuplicates(string[x-k:x]))

word = ""
number = 3452
merge_the_tools(word,number)